$(document).ready(()=>{console.log("api ready!"),$("#login").on("click",()=>{var e=$("form").serializeArray(),o=e[0].value,e=e[1].value;$.ajax({url:`http://localhost:4000/user/login/${o}/${e}`,type:"GET",crossDomain:!0,xhrFields:{withCredentials:!1},success:e=>{e&&"success"==e[0]&&(localStorage.setItem("username",e[1]),localStorage.setItem("role",e[2]),localStorage.setItem("id",e[3]),location.href="../index.html")},error:e=>{console.log(e)}})}),$("#register").on("click",()=>{var e=$("form").serializeArray();$.ajax({url:"http://localhost:4000/user/register",type:"POST",data:e,crossDomain:!0,xhrFields:{withCredentials:!1},success:e=>{e&&"success"==e&&(location.href="./login.html")},error:e=>{console.log(e)}})}),$("#forgotPass").on("click",()=>{var e=$("form").serializeArray()[0].value;$.ajax({url:`http://localhost:4000/user/forgot/${e}`,method:"GET",crossDomain:!0,xhrFields:{withCredentials:!1},success:e=>{e&&console.log(e)},error:e=>{console.log(e)}})})});