$(document).ready(()=>{function t(){var t=localStorage.getItem("id");$.ajax({url:`http://localhost:4000/user/payments/${t}`,method:"GET",crossDomain:!0,xhrFields:{withCredentials:!1},success:t=>{t&&t.forEach((t,a)=>{t=`
                                        <tr>
                                            <td>${t.name}</td>
                                            <td>${t.address}</td>
                                            <td>${t.due}</td>
                                            <td>P${t.amount}</td>
                                            <td>
                                                <a>
                                                    <button type="button" class="btn btn-primary pay">Pay Now</button>
                                                </a> 
                                                <a href="./property-info.html" >
                                                    <button type="button" id="p_${t.id}" class="btn btn-info">View Property</button>
                                                </a> 
                                            </td>
                                        </tr>
                                        `;$("#table_data").append(t)})},error:t=>{console.log(t)}})}t(),$("#table_data").on("click",".pay",t=>{t=$(t.target).attr("id").split("_")[1];console.log(t)})});